import React, {useState} from 'react';
// import { UserContext } from './context/UserContext';
// import ErrorMessage from "./ErrorMessage.js";
import axios from 'axios';

export const Register2 = () => {
    const { email, setEmail } = useState('');
    const { password, setPassword } = useState('');
    const { confirmPassword, setConfirmPassword } = useState('');
    const { full_name, setFullName } = useState('');
    // const [, setToken] = UserContext(UserContext);
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = () => {
        if (password !== confirmPassword) {
            setErrorMessage("Password and confirm password do not match");
            return;
        }
    
        axios.post('http://localhost:8000/register', { username, email, password })
            .then((response) => {
                if (response.data.access_token) {
                    console.log(response.data.access_token);
                    localStorage.setItem('access_token', response.data.access_token);
                    window.location.href = "/landing";
                }
            })
            .catch((error) => {
                if (error.response && error.response.status === 400) {
                    setErrorMessage(error.response.data.detail);
                } else {
                    setErrorMessage("An error occurred. Please try again later.");
                }
            });
    }
    
    return (
        <div className="login-box">
            <h2>Register</h2>
            <form>
                <div className="user-box">
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} name="" required />
                    <label>Username</label>
                </div>
                <div className="user-box">
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} name="" required />
                    <label>Email</label>
                </div>
                <div className="user-box">
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} name="" required />
                    <label>Password</label>
                </div>
                <div className="user-box">
                    <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} name="" required />
                    <label>Confirm Password</label>
                </div>
                {errorMessage && <p className="error-message">{errorMessage}</p>}
                <h4 style={{ color: 'white' }}>Already have an account?</h4>
                <a href="/login">Login</a>
                <a onClick={handleSubmit}>
                    <span></span>
                    <span></span>
                    <h2 style={{ color: 'white' }}>WELCOME TO OUR WEBSITE</h2>
                    <span></span>
                    Submit
                </a>
            </form>
        </div>
    );

};
export default Register2;
